async function yamapPoint(addr){
	return ymaps.geocode(addr, {        
        results: 1            
    })
}

function fadeOutnojquery(el){
	let hellopreloader = document.getElementById("hellopreloader_preload");
	let map = document.getElementById(nameMap);
	
	el.style.opacity = 1;
	let interhellopreloader = setInterval(function(){
    	el.style.opacity = el.style.opacity - 0.05;
        	if (el.style.opacity <=0.05){ 
        		clearInterval(interhellopreloader);
        		hellopreloader.style.display = "none";
        		map.style.display = "block";
    		}
    },16);
}
